mapper.select:
SELECT
*
FROM t_payment_settle_parent
WHERE 1=1

    and currency = #{queryForm.currency}


    and department_id = #{queryForm.departmentId}


    and settle_status = #{queryForm.settleStatus}


    and trade_date >= #{queryForm.settleStartDate,jdbcType=VARCHAR}
    and trade_date <= #{queryForm.settleEndDate,jdbcType=VARCHAR}

